import enumclass Border(enum.IntFlag):    EMPTY = 0    TOP = enum.auto()    BOTTOM = enum.auto()    LEFT = enum.auto()    RIGHT = enum.auto()        @property    def corner(self) -> bool:        return self in (            self.TOP | self.LEFT,            self.TOP | self.RIGHT,            self.BOTTOM | self.LEFT,            self.BOTTOM | self.RIGHT,        )        @property    def dead_end(self) -> bool:        return self.bit_count() == 3        @property    def intersection(self) -> bool:        return self.bit_count() < 2